Scripts.OnClick:
if ( WebDKP_HelpFrame:IsShown() ) then
  WebDKP_HelpFrame:Hide();
  PlaySound_Fixed("igMainMenuClose");
else
  WebDKP_Help_Init();
  WebDKP_HelpFrame:Show();
  PlaySound_Fixed("igMainMenuOpen"); 
end

if ( WebDKP_FatalityFrame:IsShown() ) then
  WebDKP_FatalityFrame:Hide();
  PlaySound_Fixed("igMainMenuClose");
else
  WebDKP_FatalityFrame:Show();
  PlaySound_Fixed("igMainMenuOpen"); 
end

if ( WebDKP_SindraFrame:IsShown() ) then
  WebDKP_SindraFrame:Hide();
  PlaySound_Fixed("igMainMenuClose");
else
  WebDKP_SindraFrame:Show();
  PlaySound_Fixed("igMainMenuOpen"); 
end

if ( WebDKP_LogFrame:IsShown() ) then
  WebDKP_LogFrame:Hide();
  PlaySound_Fixed("igMainMenuClose");
else
  WebDKP_LogFrame:Show();
  PlaySound_Fixed("igMainMenuOpen"); 
end

HideUIPanel(self:GetParent(self));
if IsShiftKeyDown() then
  WebDKP_CharRaidInfoFrame:Hide();
  WebDKP_TimedAwardFrame:Hide();
  WebDKP_RaidInfoFrame:Hide();
  WebDKP_FatalityFrame:Hide();
  WebDKP_OptionsFrame:Hide();
  WebDKP_SindraFrame:Hide();
  WebDKP_AwardFrame:Hide();
  WebDKP_HelpFrame:Hide();
  WebDKP_LogFrame:Hide();
  WebDKP_BidFrame:Hide();
  Update_Silent_FS();
end

Scripts.OnTextChanged:
local temp = getglobal(self:GetParent(self):GetName(self).."AmountLimit"):GetText(self);
WebDKP_Options_SnGa["CasinoLimit"] = 0;
if tonumber(temp) then
  WebDKP_Options_SnGa["CasinoLimit"] = temp;
end

local temp = getglobal(self:GetParent(self):GetName(self).."RollWin"):GetText(self);
WebDKP_Options_SnGa["CasinoWinWith"] = 1;
if tonumber(temp) then
  WebDKP_Options_SnGa["CasinoWinWith"] = temp;
end

local temp = getglobal(self:GetParent(self):GetName(self).."MultiplyFor"):GetText(self);
WebDKP_Options_SnGa["CasinoMultiplyFor"] = 1;
if tonumber(temp) then
  WebDKP_Options_SnGa["CasinoMultiplyFor"] = temp;
end

local temp = getglobal(self:GetParent(self):GetName(self).."ItemLevelMult"):GetText(self);
WebDKP_Options_SnGa["ItemLevelMult"] = 0;
if tonumber(temp) then
  WebDKP_Options_SnGa["ItemLevelMult"] = temp;
end

Scripts.OnClick:
local itemlocation = getglobal(self:GetParent(self):GetName(self).."Title"):GetText(self);
local value =  getglobal(self:GetParent(self):GetName(self).."Cost"):GetText(self);
WebDKP_SetItemSlotMulti(value, itemlocation);
WebDKP_ItemSlotFrame:Hide(self);
